import styled from 'styled-components';

export const StyledSearchBoxWrapper = styled.div`
  display: flex;
  flex-direction: 'row';
  justify-content: center;
  align-items: center;
  margin-right: 10px;
`;
export const StyledSearchInput = styled.input`
  color: red;
  max-width: 30vw;
  margin-right: 10px;
`;
export const StyledSearchButton = styled.button`
  color: #000000;
`;
