import styled from 'styled-components';

export const StyledPlayingListWrapper = styled.div`
  display: -webkit-box;
  flex-wrap: nowrap;
  overflow-y: hidden;
`;
export const StyledPlayingListText = styled.p`
  text-align: center;
  position: sticky;
  font-size: 3vw;
  font-weight: bolder;
  letter-spacing: 1vw;
  margin: 10px;
`;
